import styles from "./Checkout.module.css";
import { LoadingIcon } from "./Icons";
import { getProducts } from "./dataService";
import { useEffect, useState } from "react";

const Product = ({
  data,
  setQuantity,
  setTotalPrice,
  setDiscount,
  totalPrice,
  discount,
  quantity,
}) => {
  const { id, name, availableCount, price, orderedQuantity } = data;

  /** handle Quantity Increase*/
  const handleQuantityIncrease = (data) => {
    if (quantity < availableCount) {
      setTotalPrice((prev) => prev + data.price);
      setQuantity((prev) => prev + 1);
    } else
      alert("Your availableCount count is Low please Buy limited productsðŸ¤£");
  };
  /** handle Quantity Decrease*/
  const handleQuantityDecrease = (data) => {
    if (!totalPrice <= 0) {
      setTotalPrice((prev) => prev - data.price);
      setQuantity((prev) => prev - 1);
    } else {
      alert("Negative Value Not Allaw");
    }
  };

  return (
    <tr>
      <td>{id}</td>
      <td>{name}</td>
      <td>{availableCount}</td>
      <td>${price}</td>
      <td>{quantity}</td>
      <td>{totalPrice}</td>
      <td>
        <button
          onClick={() => handleQuantityIncrease(data)}
          className={styles.actionButton}
        >
          +
        </button>
        <button
          onClick={() => handleQuantityDecrease(data)}
          className={styles.actionButton}
        >
          -
        </button>
      </td>
    </tr>
  );
};

const Checkout = () => {
  const [data, setData] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  useEffect(() => {
    getProducts().then((res) => setData(res));
  }, []);

  console.log(data);
  return (
    <div>
      <header className={styles.header}>
        <h1>Electro World</h1>
      </header>
      <main>
        <LoadingIcon />
        <table className={styles.table}>
          <thead>
            <tr>
              <th>Product ID</th>
              <th>Product Name</th>
              <th># Available</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Total</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {data.map((p, index) => (
              <Product key={index} data={p} />
            ))}
          </tbody>
        </table>
        <h2>Order summary</h2>
        <p>Discount: $</p>
        <p>Total: $ </p>
      </main>
    </div>
  );
};

export default Checkout;
